import ReviewList from "./ReviewList";
import items from "../mock.json";
import {useState} from "react";
function App() {
  const [order, setOrder] = useState("createdAt");
  const sortedItems = items.sort((a, b) => b[order] - a[order]);
  const handleClick_new = () => setOrder("createdAt");
  const handleClick_best = () => setOrder("rating");

  return (
    <div>
      <div>
        <button onClick={handleClick_new}>최신순</button>
        <button onClick={handleClick_best}>베스트순</button>
      </div>
      <ReviewList items={sortedItems} />
    </div>
  );
}

export default App;
